import './App.css';
import Die from './Die.js';
import React from 'react';

function App() {

  const [arrayOfNumbers, setArrayOfNumbers] = React.useState(allnewdice())

  function allnewdice() {
    let num = []
    for (let i = 0; i < 10; i++) {
      num.push({value : Math.floor(Math.random() * 6) + 1, isHeld:false})
    }
    return num
  }
  function rollnewdice(){
    setArrayOfNumbers(allnewdice())
  }

  function handlefreeze(index){
    console.log("clicked");
  }

  const dieelements = arrayOfNumbers.map((die, index) => <Die key={index} value={die.value} onPress={()=> handlefreeze(die.isHeld)}/>)
  return (
    <div className="App">
      <div className='Tenzies-outlerlayer'>
        <div className='Tenzies-innerlayer'>
          <h2 className='Title'>Tenzies</h2>
          <p className='Paragraph'>Roll until all dice are the same. Click each die to freeze it at its current value between rolls.</p>
          <div className='Dice'>
            {dieelements}
          </div>
          <button className='submit-button' onClick={rollnewdice}>Roll</button>
        </div>
      </div>
    </div >
  );
}

export default App;
