import './App.css';
import Die from './Die.js';

function App() {

  let arrayOfNumbers = allnewdice()

  function allnewdice() {
    let num = []
    for (let i = 0; i < 10; i++) {
      num.push(Math.floor(Math.random() * 6) + 1)
    }
    return num
  }

  return (
    <div className="App">
      <div className='Tenzies-outlerlayer'>
        <div className='Tenzies-innerlayer'>
          <h2 className='Title'>Tenzies</h2>
          <p className='Paragraph'>Roll until all dice are the same. Click each die to freeze it at its current value between rolls.</p>
          <div className='Dice'>
            {arrayOfNumbers.map((item , index) => {
              return (
                  <Die key={index} value={item} />
              )
            })}
          </div>
          <button className='submit-button'>Roll</button>
        </div>
      </div>
    </div >
  );
}

export default App;
